package org.khronos.collada.validation;

import org.khronos.collada.TargetableFloat;
import org.khronos.collada.TargetableFloat3;

/**
 * A sample validator interface for {@link org.khronos.collada.SpotType}.
 * This doesn't really do anything, and it's not a real EMF artifact.
 * It was generated by the org.eclipse.emf.examples.generator.validator plug-in to illustrate how EMF's code generator can be extended.
 * This can be disabled with -vmargs -Dorg.eclipse.emf.examples.generator.validator=false.
 */
public interface SpotTypeValidator {
	boolean validate();

	boolean validateColor(TargetableFloat3 value);
	boolean validateConstantAttenuation(TargetableFloat value);
	boolean validateLinearAttenuation(TargetableFloat value);
	boolean validateQuadraticAttenuation(TargetableFloat value);
	boolean validateFalloffAngle(TargetableFloat value);
	boolean validateFalloffExponent(TargetableFloat value);
}
